import React, { useState } from 'react';
import { StandardRadioButtons } from './components/StandardRadioButtons';
import { getIcon } from './utilities/IconProvider';

export const RadioExample = () => {
  const [value, setValue] = useState('option1');

  const options = [
    { value: 'option1', label: 'Option 1' },
    { value: 'option2', label: 'Option 2' },
    { value: 'option3', label: 'Option 3', icon: getIcon('star') },
  ];

  return (
    <StandardRadioButtons
      options={options}
      selectedValue={value}
      onChange={setValue}
      label="Choose an option"
      layout="horizontal"
      variant="icon"
      tooltip="Select one of the options"
    />
  );
};
