function InnerThemeWrapper({ children }) {
  const { darkTheme: isDark } = useDarkMode();
  const theme = {
    ...baseTheme,
    ...(isDark ? darkTheme : lightTheme),
  };
  // return ThemeProvider or your component here
  return <ThemeProvider theme={theme}>{children}</ThemeProvider>;
}

